import React, { Component } from 'react';
import axios from "axios";

class RestaurantUpationbyID extends Component {
  initialState = {
    id :"",
      name: "",
      address: "",
      addressline2: "",
      url: "",
      outcode: "",
      postcode: "",
      rating: "",
      typeoffood: "",

      errors: [],
    restaurantList:[]
  };

  state = this.initialState;
  FindRestaurantByID=()=>{
    const {id } = this.state;
    axios.get("http://localhost:8080/restaurant/"+this.state.id).then(response => {
      this.setState({restaurantList : response.data.responseData},{name : response.data.responseData.name})
    })
  }

    UpdateRestaurantByID=()=>{
      axios.put('http://localhost:8080/restaurant/'+this.state.id, 
      {
        name: this.state.name, 
        address: this.state.address, 
        addressline2: this.state.addressline2,
        url:this.state.url,
        outcode:this.state.outcode,
        postcode:this.state.postcode,
        rating:parseInt(this.state.rating, 10),
        type_of_food:this.state.typeoffood

      }, 
      {header: {
        'Access-Control-Allow-Origin': '*',
        'Content-Type': 'application/json',
    }},
      )
      .then(function (response) {
          console.log(response);
      })
      .catch(function (error) {
          console.log(error);
  });
  this.setState({
    name: '',
    address: '',
    addressline2: '',
    url: '',
    outcode: '',
    postcode: '',
    rating: '',
    typeoffood: ''
  });
  alert("Restaurant Data Updated")
    }


  render() {
    return (
      <form>
      <input
          value={this.state.id}
          onChange={evt => this.setState({ id: evt.target.value })}
          type="text"
          placeholder="Enter Restaurant ID"
        />
        <button type="button" 
                onClick={()=>{
                console.log("click post");
                this.FindRestaurantByID()}}>Find Restaurant</button>
       <input
          defaultValue={this.state.restaurantList.name}
          onChange={evt => this.setState({ name: evt.target.value })}
          type="text"
          placeholder="Name"
        />
        <input
          value={this.state.address}
          onChange={evt => this.setState({ address: evt.target.value })}
          type="text"
          placeholder="Address"
        />
        <input
          value={this.state.restaurantList.addressline2}
          onChange={evt => this.setState({ addressline2: evt.target.value })}
          type="text"
          placeholder="Address Line 2"
        />
        <input
          defaultValue={this.state.restaurantList.url}
          onChange={evt => this.setState({ url: evt.target.value })}
          type="text"
          placeholder="URL"
        />
        <input
          defaultValue={this.state.restaurantList.outcode}
          onChange={evt => this.setState({ outcode: evt.target.value })}
          type="text"
          placeholder="Outcode"
        />
        <input
          defaultValue={this.state.restaurantList.postcode}
          onChange={evt => this.setState({ postcode: evt.target.value })}
          type="text"
          placeholder="Postcode"
        />
        <input
          defaultValue={this.state.restaurantList.rating}
          onChange={evt => this.setState({ rating: evt.target.value })}
          type="number"
          placeholder="Rating"
        />
        <input
          defaultValue={this.state.restaurantList.type_Of_food}
          onChange={evt => this.setState({ typeoffood: evt.target.value })}
          type="text"
          placeholder="Type Of Food"
        /> 
        <button type="button" 
                onClick={()=>{
                console.log("click post");
                this.UpdateRestaurantByID()}}>Update Restaurant</button>
      </form>
    );
  }
}

export default RestaurantUpationbyID;
