import React, { Component } from "react";
import "./cra.css";

const formValid = ({ formErrors, status, responseerror, ...rest }) => {
  let valid = true;

  // validate form errors being empty
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });

  // validate the form was filled out
  Object.values(rest).forEach(val => {
    val === null && (valid = false);
  });

  return valid;
};

class restaurantRegistration extends Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: null,
      address: null,
      addressline2: null,
      url: null,
      outcode: null,
      postcode: null,
      rating: null,
      type_of_food: null,
      formErrors: {
        firstName: "",
        address: "",
        addressline2: "",
        url: "",
        outcode: "",
        postcode: "",
        rating: "",
        type_of_food: ""
      },
      status: null,
      responseerror: null
    };
  }

  registration = () =>{
    return fetch("http://localhost:8080/restaurant/",{mode :'no-cors'}, {
        method: "POST",
        headers: {
          "Access-Control-Allow-Credentials": true,
          "Access-Control-Allow-Origin": "*",
      //   Accept: "application/json",
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          name: this.state.firstName,
          addressline2: this.state.addressline2,
          address: this.state.address,
          url: this.state.url,
          outcode: this.state.outcode,
          postcode: this.state.postcode,
          rating: parseInt(this.state.rating, 10), //it convert string to int
          type_of_food: this.state.type_of_food
        })
      })
        .then(response => response.json())
        .then(responseJson => {
          console.log("response", responseJson);
          if (responseJson.status) {
            this.setState({
              status: responseJson.status
            });
          } else {
            this.setState({
              status: responseJson.status,
              responseerror: responseJson.message
            });
          }
          console.log("state", this.state);
        })
        .catch(error => {
          console.error("error", error);
        });
  }

  handleSubmit = e => {
  //  e.preventDefault();
    this.registration()
    if (formValid(this.state)) {
      console.log("--SUBMITTING", this.state);

    }
      else {
        console.error("FORM INVALID - DISPLAY ERROR MESSAGE");
      }
    };


  handleChange = e => {
    e.preventDefault();
    const { name, value } = e.target;
    let formErrors = { ...this.state.formErrors };

    switch (name) {
      case "firstName":
        formErrors.firstName =
          value.length < 3 ? "minimum 3 characaters required" : "";
        break;
      case "address":
        formErrors.address =
          value.length < 3 ? "minimum 3 characaters required" : "";
        break;
      case "addressline2":
        formErrors.addressline2 =
          value.length < 3 ? "minimum 3 characaters required" : "";
        break;
      case "url":
        formErrors.url =
          value.length < 3 ? "minimum 3 characaters required" : "";
        break;
      case "outcode":
        formErrors.outcode =
          value.length < 3 ? "minimum 3 characaters required" : "";
        break;
      case "postcode":
        formErrors.postcode =
          value.length < 3 ? "minimum 3 characaters required" : "";
        break;
      case "rating":
        formErrors.rating =
          value.length < 0 ? "minimum 3 characaters required" : "";
        break;
      case "type_of_food":
        formErrors.type_of_food =
          value.length < 3 ? "minimum 3 characaters required" : "";
        break;
      default:
        break;
    }

    this.setState({ formErrors, [name]: value }, () => console.log(this.state));
  };

  render() {
    const { formErrors } = this.state;

    return (
      <div className="wrapper">
        <div className="form-wrapper">
          <h1>Create Account</h1>
          {/* <form onSubmit={this.handleSubmit} noValidate> */}
          <form >
            <div className="email">
              <label htmlFor="firstName">First Name</label>
              <input
                className={formErrors.firstName.length > 0 ? "error" : null}
                placeholder="First Name"
                type="text"
                name="firstName"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.firstName.length > 0 && (
                <span className="errorMessage">{formErrors.firstName}</span>
              )}
            </div>
            <div className="email">
              <label htmlFor="firstName">Address</label>
              <input
                className={formErrors.address.length > 0 ? "error" : null}
                placeholder="Address"
                type="text"
                name="address"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.address.length > 0 && (
                <span className="errorMessage">{formErrors.address}</span>
              )}
            </div>
            <div className="email">
              <label htmlFor="firstName">address Line 2</label>
              <input
                className={formErrors.firstName.length > 0 ? "error" : null}
                placeholder="Address Line 2"
                type="text"
                name="addressline2"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.addressline2.length > 0 && (
                <span className="errorMessage">{formErrors.addressline2}</span>
              )}
            </div>
            <div className="email">
              <label htmlFor="firstName">Url</label>
              <input
                className={formErrors.url.length > 0 ? "error" : null}
                placeholder="Url"
                type="text"
                name="url"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.url.length > 0 && (
                <span className="errorMessage">{formErrors.url}</span>
              )}
            </div>
            <div className="email">
              <label htmlFor="firstName">Outcode</label>
              <input
                className={formErrors.outcode.length > 0 ? "error" : null}
                placeholder="Outcode"
                type="text"
                name="outcode"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.outcode.length > 0 && (
                <span className="errorMessage">{formErrors.outcode}</span>
              )}
            </div>
            <div className="email">
              <label htmlFor="firstName">postcode</label>
              <input
                className={formErrors.postcode.length > 0 ? "error" : null}
                placeholder="Postcode"
                type="text"
                name="postcode"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.postcode.length > 0 && (
                <span className="errorMessage">{formErrors.postcode}</span>
              )}
            </div>

            <div className="email">
              <label htmlFor="firstName">Rating</label>
              <input
                className={formErrors.rating.length > 0 ? "error" : null}
                placeholder="Rating"
                type="text"
                name="rating"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.rating.length > 0 && (
                <span className="errorMessage">{formErrors.rating}</span>
              )}
            </div>

            <div className="email">
              <label htmlFor="firstName">Food Type</label>
              <input
                className={formErrors.type_of_food.length > 0 ? "error" : null}
                placeholder="Food type"
                type="text"
                name="type_of_food"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.type_of_food.length > 0 && (
                <span className="errorMessage">{formErrors.type_of_food}</span>
              )}
            </div>

            <div className="createAccount">
              <button 
                type="button" 
                onClick={()=>{
                console.log("click post");
                this.handleSubmit()
                }
              }>Submit</button>
            </div>
          </form>
        </div>
        {this.state.status && (
          <span className="alert alert-success">Submited succesfully</span>
        )}
        {this.state.status === false && (
          <span className="alert alert-danger">{this.state.responseerror}</span>
        )}
      </div>
    );
  }
}
export default restaurantRegistration;
